package com.appstore.utils;

import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import java.util.Properties;

import com.appstore.interfaces.DataBase;

public class DataBaseService implements DataBase{

	GetDBProperties propertiesDB=new GetDBProperties();
    private PreparedStatement statement = null;  
    private Connection conn=null;  
    private ResultSet rs = null;  
	public Connection getConn(){
		
		 try  
         {  
             Class.forName(propertiesDB.getProperty("dbDriver"));  
         }  
         catch (ClassNotFoundException e)  
         {  
        	 System.err.println(  "装载 JDBC驱动程序失败。" ); 
             e.printStackTrace();  
         }  
         try  
         {  
             conn = DriverManager.getConnection(propertiesDB.getProperty("dbUrl"),propertiesDB.getProperty("dbUser"),propertiesDB.getProperty("dbPass"));
         } 
         catch (SQLException e)  
         {  
        	 System.err.println( "无法连接数据库" );  
             e.printStackTrace();  
         }  
         return conn;  	
	}

	@Override
	public boolean InsertDML(String sql) {
		  try {  
	            statement = conn.prepareStatement(sql);  
	            statement.executeUpdate();  
	            return true;  
	        } catch (SQLException e) {  
	            System.out.println("插入数据库时出错：");  
	            e.printStackTrace();  
	        } catch (Exception e) {  
	            System.out.println("插入时出错：");  
	            e.printStackTrace();  
	        }  
	        return false;  
		
	}

	@Override
	public boolean DeleDML(String sql) {
		 try {  
	            statement = conn.prepareStatement(sql);  
	            statement.executeUpdate();  
	            return true;  
	        } catch (SQLException e) {  
	            System.out.println("插入数据库时出错：");  
	            e.printStackTrace();  
	        } catch (Exception e) {  
	            System.out.println("插入时出错：");  
	            e.printStackTrace();  
	        }  
	        return false; 
		
	}

	@Override
	public boolean UpdateDML(String sql) {
		 try {  
	            statement = conn.prepareStatement(sql);  
	            statement.executeUpdate();  
	            return true;  
	        } catch (SQLException e) {  
	            System.out.println("插入数据库时出错：");  
	            e.printStackTrace();  
	        } catch (Exception e) {  
	            System.out.println("插入时出错：");  
	            e.printStackTrace();  
	        }  
	        return false;  
	        
	    }  
		
	

	@Override
	public ResultSet QueryDML(String sql) {
	        try {  
	            statement = getConn().prepareStatement(sql);  
	            rs = statement.executeQuery(sql);  
	        } catch (SQLException e) {  
	            e.printStackTrace();  
	        }  
	        return rs;  
	}

	@Override
	public void CloseAll() {                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       

		//关闭conn
		if(conn!=null){
			try {
				conn.close();
			} catch (SQLException e) {
				System.out.println("conn资源关闭失败");
			}
			finally {
				conn=null;
			}
		}
		
		//关闭result
		if(rs!=null){
			try {
				rs.close();
			} catch (SQLException e) {
				System.out.println("rs资源关闭失败");
			}
			finally {
				rs=null;
			}
		}
		
		//关闭statement
		if(statement!=null){
			try {
				statement.close();
			} catch (SQLException e) {
				System.out.println("statement资源关闭失败");
			}
			finally {
				statement=null;
			}
		}
	
	}
	
}
